
function toggleMobileMenu(){const menu=document.getElementById('mobile-menu');menu.classList.toggle('active')}function closeMobileMenu(){const menu=document.getElementById('mobile-menu');menu.classList.remove('active')}function scrollToSection(sectionId){const element=document.getElementById(sectionId);if(element){element.scrollIntoView({behavior:'smooth'});closeMobileMenu()}}function handleFormSubmit(event){event.preventDefault();const form=event.target;const formData=new FormData(form);const name=formData.get('name');const email=formData.get('email');const phone=formData.get('phone')||'не указан';const message=formData.get('message');const emailBody=`Имя: ${name}%0AEmail: ${email}%0AТелефон: ${phone}%0A%0AСообщение:%0A${message}`;const mailtoLink=`mailto:6481736@docservis.ru?subject=Новое сообщение с сайта&body=${emailBody}`;window.location.href=mailtoLink;form.reset()}document.addEventListener('DOMContentLoaded',function(){const observerOptions={threshold:0.1,rootMargin:'0px 0px -50px 0px'};const observer=new IntersectionObserver(function(entries){entries.forEach(entry=>{if(entry.isIntersecting){entry.target.style.opacity='1';entry.target.style.transform='translateY(0)'}})},observerOptions);document.querySelectorAll('section').forEach(section=>{section.style.opacity='0';section.style.transform='translateY(20px)';section.style.transition='opacity 0.6s ease, transform 0.6s ease';observer.observe(section)});const navLinks=document.querySelectorAll('a[href^="#"]');navLinks.forEach(link=>{link.addEventListener('click',function(e){e.preventDefault();const targetId=this.getAttribute('href').substring(1);const targetElement=document.getElementById(targetId);if(targetElement){targetElement.scrollIntoView({behavior:'smooth'});closeMobileMenu()}})});window.addEventListener('scroll',function(){const header=document.getElementById('header');if(window.scrollY>50){header.style.background='rgba(255, 255, 255, 0.95)'}else{header.style.background='rgba(255, 255, 255, 0.9)'}});const stats=document.querySelectorAll('.stat-number, .kpi-value');const animateNumbers=()=>{stats.forEach(stat=>{const target=parseInt(stat.textContent.replace(/[^0-9]/g,''));if(target>0){let current=0;const increment=target/50;const timer=setInterval(()=>{current+=increment;if(current>=target){current=target;clearInterval(timer)}stat.textContent=stat.textContent.replace(/[0-9]+/,Math.floor(current).toString())},30)}})};const statsObserver=new IntersectionObserver(function(entries){entries.forEach(entry=>{if(entry.isIntersecting){animateNumbers();statsObserver.unobserve(entry.target)}})},{threshold:0.5});document.querySelectorAll('.hero-stats, .kpi-section').forEach(section=>{statsObserver.observe(section)})});
